<div class="container mt-4" style="background: #ffbf4f">
  <div class="row">
    <div class="col-md-4 mb-4">
      <div id="app" class="card bg-warning text-white" >
        <img class="card-img-top" [src]="piatto?.immagine" alt="Card image cap">

      </div>
    </div>
    <div class="col-md-8 mb-4">
      <div class="card">
        <div class="card-body">
          <h5 class="card-title">{{ piatto?.nome}} </h5>
          <p class="card-text">{{piatto?.descrizione}} </p>
        </div>
      </div>

        <div class="card-body">

          <a [routerLink]="['/html', piatto?.nome]" *ngIf="isNotBevanda()">Vedi Dettagli</a>
        </div>

      </div>
    </div>


  </div>
