import { Component } from '@angular/core';
import { AuthServiceService } from '../services/auth-service.service';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent {
  constructor(private authService: AuthServiceService){}

  isAuthenticated(){    
    return this.authService.isAuthenticated();
  }
  doLogout(){
    console.log("click");
    this.authService.logout();
  }
}
<header>
    <div id="unDiv">
        <img id="logo" class="round" src="assets/images/ristorante.png" width="25%" />
    </div>
    <h1>Sito gestione recensioni ristorante</h1>
    <h2>Qui gestiamo le recensioni dei ristoranti</h2>
    <nav>
        <ul>
            <li>
                <a routerLink="/">Home</a>
            </li>
            <li>
                <a routerLink="/chi_siamo">
                    Chi siamo
                </a>
            </li>
            <li><a href="http://localhost:8080/ristoranti/dammiRistoranti">Ristoranti</a></li>
            <li *ngIf="isAuthenticated()">
                <a routerLink="/recensioni">
                    Recensioni
                </a>
            </li>

            <!-- if -->

            <li *ngIf="!isAuthenticated()"> <!-- se non è autenticato -->
                <a routerLink="/login">Login</a> <!-- mostra il link per il login -->
            </li>
            <li *ngIf="isAuthenticated()"> <!-- se è autenticato -->
                <a href="#" (click)="doLogout()">Logout</a> <!-- mostra il link per il logout -->
            </li>

            <li>Registrati</li>
        </ul>
    </nav>
</header>
