import { Component } from '@angular/core';
import { FormControl } from '@angular/forms';
import { AuthServiceService } from '../services/auth-service.service';
import {UtenteServiceService} from "../services/utente-service.service";
import {Router} from "@angular/router";
import {Observable} from "rxjs";


@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  username = new FormControl();
  password = new FormControl();
  isOK: boolean | null = null;
  tipo: number | null = null;




  constructor(private auth:AuthServiceService, private user1:UtenteServiceService, private router:Router){}

  faiLogin(){
    var user = this.username.value;
    var pass = this.password.value;

    this.isOK = this.user1.checkUtente(user);


    if (this.isOK){
      this.tipo = this.user1.getTipo(user);


      console.log(this.tipo)
    }
    else {
      alert("Username non valido")
    }

  }


}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Login - Ristorante</title>
  <style>
    body {
      font-family: Arial, sans-serif;

      margin: 0;
      display: flex;
      align-items: center;
      justify-content: center;
      height: min-content;

    }


    .login-container h2 {
      color: #343a40;
    }

    .form-group {
      margin-bottom: 20px;
    }

    .form-group label {
      display: block;
      margin-bottom: 5px;
      color: #495057;
    }

    .form-group input {
      width: 100%;
      padding: 8px;
      box-sizing: border-box;
      border: 1px solid #ced4da;
      border-radius: 4px;
    }

    .form-group button {
      background-color: #007bff;
      color: #ffffff;
      padding: 10px 15px;
      border: none;
      border-radius: 4px;
      cursor: pointer;
    }

    .form-group button:hover {
      background-color: #0056b3;
    }
  </style>
</head>
<body>

<div class="login-container">
  <h2>Login - Ristorante</h2>
  <form (ngSubmit)="faiLogin()">
    <div class="form-group">
      <label for="username">Username</label>
      <input type="text" id="username" name="username" required [formControl]="username">
    </div>
    <div class="form-group">
      <label for="password">Password</label>
      <input type="password" id="password" name="password" required [formControl]="password">
    </div>
    <div class="form-group">
      <button type="submit">Accedi</button>
    </div>

  </form>
</div>

</body>
</html>
