import { Injectable } from '@angular/core';
import {HttpClient, HttpHeaders} from "@angular/common/http";
import {AuthServiceService} from "./auth-service.service";
import {Observable} from "rxjs";
import {Router} from "@angular/router";

@Injectable({
  providedIn: 'root'
})
export class UtenteServiceService {
  private backendUrl = "http://localhost:8080"


  constructor(private http:HttpClient,private auth:AuthServiceService, private router: Router) {  }

  checkUtente(username:string){
    var header  = {
      headers: new HttpHeaders().set('Authorization', 'Basic ' + this.auth.token)
    }
    console.log("entrato")
    console.log(username)
    console.log(this.backendUrl+ "/checkUtente/" + username)
    this.http.get<boolean>(this.backendUrl+ "/checkUtente/" + username, header).subscribe(isUser => {
      console.log(isUser)
      if(isUser){
        this.router.navigate(["/login"])
      }
    })


  }
}
