import {Component, OnInit} from '@angular/core';
import {ActivatedRoute} from "@angular/router";

@Component({
  selector: 'app-html-create',
  templateUrl: './html-create.component.html',
  styleUrls: ['./html-create.component.css']
})
export class HtmlCreateComponent implements OnInit{
  name: string | null = null;

  constructor(private route: ActivatedRoute) {}

  ngOnInit(): void {
    // Retrieve the ID parameter from the route
    this.name = this.route.snapshot.paramMap.get('name');

    // Now 'this.id' contains the ID value
    console.log('ID:', this.name);
  }
}
<!DOCTYPE html>
<html lang="it">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Ricetta del piatto delizioso</title>
  <style>


    .card {
      border: 1px solid #ccc;
      border-radius: 8px;
      margin-bottom: 20px;
      padding: 20px;
      box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
    }

    img {
      max-width: 100%;
      height: auto;
      border-radius: 8px;
    }
  </style>
</head>
<body class="app-routing-module-container">

<div class="card mb-3" style="max-width: 540px;">
  <div class="row g-0">
    <div class="col-md-4">
      <img src="https://i.ibb.co/28PScvn/5.jpg" class="img-fluid rounded-start" style="width: 540pz" >
    </div>
    <div class="col-md-8">
      <div class="card-body">
        <h5 class="card-title">Card title</h5>
        <p class="card-text">This is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
        <p class="card-text"><small class="text-muted">Last updated 3 mins ago</small></p>
      </div>
    </div>
  </div>
</div>
<!-- Card degli ingredienti -->
<div class="card">
  <h2>Ingredienti:</h2>
  <ul>
    <li>200g di ingredienti principali</li>
    <li>50g di ingrediente numero uno</li>
    <li>1 cucchiaino di ingrediente segreto</li>
    <!-- Aggiungi altri ingredienti secondo necessità -->
  </ul>
</div>

<!-- Card della procedura di preparazione -->
<div class="card">
  <h2>Procedura di preparazione:</h2>
  <ol>
    <li>Passo 1: Inizia con...</li>
    <li>Passo 2: Aggiungi...</li>
    <li>Passo 3: Cuoci a fuoco medio per...</li>
    <!-- Aggiungi altri passaggi secondo necessità -->
  </ol>
</div>

</body>
</html>
