import { Component } from '@angular/core';
import { AuthServiceService } from '../services/auth-service.service';

declare function getRandomPhoto(): string; 

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})


export class HeaderComponent {
  
  constructor(private authService: AuthServiceService){}

  getRandomPhotoUrl(){
    console.log(getRandomPhoto());
    return getRandomPhoto();
  }

  isAuthenticated(){    
    return this.authService.isAuthenticated();
  }
  doLogout(){
    console.log("click");
    this.authService.logout();
  }
}
<!--Main Navigation-->
<header>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Menu a Tendina</title>
  <style>
    body {
      font-family: Arial, sans-serif;
    }

    .dropdown {
      position: relative;
      display: inline-block;
      top:0px;
      right: 15px;

    }

    .dropdown-content {
      display: none;
      position: absolute;
      background-color: #f9f9f9;
      min-width: 160px;
      right: 0;
      box-shadow: -8px 8px 16px rgba(0, 0, 0, 0.2);
      z-index: 1;
      top: 30px;
    }

    .dropdown:hover .dropdown-content {
      display: block;
    }

    .dropdown-item {
      padding: 12px 16px;
      display: block;
      text-decoration: none;
      color: #333;
      cursor: pointer;
    }

    .dropdown-item:hover {
      background-color: #f1f1f1;
    }

    .icon {
      margin-right: 8px;
    }
  </style>


  <!-- Navbar -->
  <nav
    id="main-navbar"
    class="navbar navbar-expand-lg navbar-light bg-white fixed-top"
  >
    <!-- Container wrapper -->
    <div class="container-fluid">
      <!-- Toggle button -->
      <button
        class="navbar-toggler"
        type="button"
        data-mdb-toggle="collapse"
        data-mdb-target="#sidebarMenu"
        aria-controls="sidebarMenu"
        aria-expanded="false"
        aria-label="Toggle navigation"
      >
        <i class="fas fa-bars"></i>
      </button>

      <!-- Brand -->
      <a class="navbar-brand" href="#">
        <img
          src="https://i.ibb.co/PmDn4H2/chef-1.png"
          height="30"

          alt=""
          loading="lazy"
        />
      </a>

      <!-- Search form -->
      <form class="d-none d-md-flex input-group w-auto my-auto">
        <input
          autocomplete="off"
          type="search"
          class="form-control rounded"
          placeholder='Search (ctrl + "/" to focus)'
          style="min-width: 225px"
        />
        <span class="input-group-text border-0"
        ><i class="fas fa-search"></i
        ></span>
      </form>

      <!-- Right links -->
      <ul class="navbar-nav ms-auto d-flex flex-row">
        <!-- Notification dropdown -->


        <!-- Icon -->

        <!-- Icon dropdown -->
        <!-- Avatar -->
        <div class="dropdown" *ngIf="isAuthenticated()">
          <img
            src="https://i.ibb.co/KmMjqPw/user.png"
            height="30"

            alt=""
            loading="lazy"
          />

          <div class="dropdown-content">

            <a routerLink="preferiti" class="dropdown-item">
              <svg xmlns="http://www.w3.org/2000/svg" width="25" height="25" fill="currentColor" class="bi bi-star-fill" viewBox="0 0 16 16">
                <path  d="M2.866 14.85c-.078.444.36.791.746.593l4.39-2.256 4.389 2.256c.386.198.824-.149.746-.592l-.83-4.73 3.522-3.356c.33-.314.16-.888-.282-.95l-4.898-.696L8.465.792a.513.513 0 0 0-.927 0L5.354 5.12l-4.898.696c-.441.062-.612.636-.283.95l3.523 3.356-.83 4.73z"/>
              </svg>
              <span class="icon">&#x1F513;</span> Preferiti</a>

            <a (click)="doLogout()" class="dropdown-item"><span class="icon">&#x1F513;</span> Logout</a>
          </div>
        </div>
      </ul>
    </div>
    <!-- Container wrapper -->
  </nav>
  <!-- Navbar -->
</header>
<!--Main Navigation-->

<!--Main layout-->
<main style="margin-top: 300px">
  <div class="container pt-4">

  </div>
</main>
<!--Main layout-->
