import { Component } from '@angular/core';
import { FormControl } from '@angular/forms';
import { AuthServiceService } from '../services/auth-service.service';
import {UtenteServiceService} from "../services/utente-service.service";
import {Router} from "@angular/router";
import {Observable} from "rxjs";


@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  username = new FormControl();
  password = new FormControl();

  isExist: Observable<boolean>;


  constructor(private auth:AuthServiceService, private user1:UtenteServiceService, private router:Router, isExist = false){}

  faiLogin(){
    var user = this.username.value;
    var pass = this.password.value;
    console.log(user);

    var check = this.user1.checkUtente(user);
    console.log(check);
    if (check){
      this.router.navigate(['/antipasti']);

    }
    else {
      this.router.navigate(['/login']);
    }

  }


}
