import {Component, Input} from '@angular/core';
import {Piatto} from "../model/piatto";
import {PiattiServiceService} from "../piatti-service.service";

@Component({
  selector: 'app-piatti-vetrina',
  templateUrl: './piatti-vetrina.component.html',
  styleUrls: ['./piatti-vetrina.component.css']
})
export class PiattiVetrinaComponent {
  @Input()piatto?: Piatto;


}
<!-- Add Bootstrap CSS and JS to your HTML file -->
<link rel="stylesheet" href="https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css">
<script src="https://code.jquery.com/jquery-3.3.1.slim.min.js"></script>
<script src="https://cdnjs.cloudflare.com/ajax/libs/popper.js/1.14.7/umd/popper.min.js"></script>
<script src="https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/js/bootstrap.min.js"></script>

<script src="https://cdn.jsdelivr.net/npm/vue@2"></script>

<!-- Your HTML content -->
<div class="container mt-4">
  <div class="row">
    <div class="col-md-4 mb-4">
      <div id="app" class="card bg-warning text-white" >
        <img  src = "{{linkAsString}}" class="card-img-left" alt="Placeholder Image">
      </div>
    </div>
    <div class="col-md-8 mb-4">
      <div class="card">
        <div class="card-body">
          <h5 class="card-title">{{piatto?.immagine}} </h5>
          <p class="card-text">{{piatto?.immagine}} </p>
        </div>
        <ul class="list-group list-group-flush">
          <li class="list-group-item">Ingredient 1</li>
          <li class="list-group-item">Ingredient 2</li>
          <!-- Add more ingredients as needed -->
        </ul>
        <div class="card-body">
          <a [routerLink]="['/html', piatto?.nome]">See Details</a>
        </div>
      </div>
    </div>


  </div>
  <script>
    new Vue({
      el: '#app',
      data: {
        link: piatto?.immagine
      },
      computed: {
        // Utilizzo di una proprietà calcolata per ottenere una stringa da link
        linkAsString: function() {
          return this.link.toString();
        }
      }
    });
  </script>

</div>
