import { Component } from '@angular/core';
declare function updateMeter(): void;

@Component({
  selector: 'app-registrazione',
  templateUrl: './registrazione.component.html',
  styleUrls: ['./registrazione.component.css']
})
export class RegistrazioneComponent {

}
<!DOCTYPE html>
<html lang="en">

<style>
  @import url('https://fonts.googleapis.com/css?family=Roboto&display=swap');
  @import url('https://stackpath.bootstrapcdn.com/font-awesome/4.7.0/css/font-awesome.min.css');
  *{ margin: 0; padding: 0;}
  *, *::before, *::after {
    margin: 0;
    padding: 0;
    box-sizing: inherit;
  }

  body{
    min-height: 100vh;
    display: flex;
    justify-content: center;
    flex-wrap: wrap;
    align-content: flex-start;

    font-family: 'Roboto', sans-serif;
    font-style: normal;
    font-weight: 300;
    font-smoothing: antialiased;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    font-size: 15px;
    background: #eee;
  }
  .cd__intro{
    padding: 60px 30px;
    margin-bottom: 15px;
    flex-direction: column;

  }
  .cd__intro,
  .cd__credit{
    display: flex;
    width: 100%;
    justify-content: center;
    align-items: center;
    background: #fff;
    color: #333;
    line-height: 1.5;
    text-align: center;
  }

  .cd__intro h1 {
    font-size: 18pt;
    padding-bottom: 15px;

  }
  .cd__intro p{
    font-size: 14px;
  }

  .cd__action{
    text-align: center;
    display: block;
    margin-top: 20px;
  }

  .cd__action a.cd__btn {
    text-decoration: none;
    color: #666;
    border: 2px solid #666;
    padding: 10px 15px;
    display: inline-block;
    margin-left: 5px;
  }
  .cd__action a.cd__btn:hover{
    background: #666;
    color: #fff;
    transition: .3s;
    -webkit-transition: .3s;
  }
  .cd__action .cd__btn:before{
    font-family: FontAwesome;
    font-weight: normal;
    margin-right: 10px;
  }
  .down:before{content: "\f019"}
  .back:before{content:"\f112"}

  .cd__credit{
    padding: 12px;
    font-size: 9pt;
    margin-top: 40px;

  }
  .cd__credit span:before{
    font-family: FontAwesome;
    color: #e41b17;
    content: "\f004";


  }
  .cd__credit a{
    color: #333;
    text-decoration: none;
  }
  .cd__credit a:hover{
    color: #1DBF73;
  }
  .cd__credit a:hover:after{
    font-family: FontAwesome;
    content: "\f08e";
    font-size: 9pt;
    position: absolute;
    margin: 3px;
  }
  .cd__main{
    background: #fff;
    padding: 20px;
    flex-direction: row;
    flex-wrap: wrap;
    justify-content: center;

  }
  .cd__main{
    display: flex;
    width: 100%;
  }

  @media only screen and (min-width: 1360px){
    .cd__main{
      max-width: 1280px;
      margin-left: auto;
      margin-right: auto;
      padding: 24px;
    }
  }
</style>
<head>
  <meta charset="utf-8">
  <meta name="viewport" content="width=device-width, initial-scale=1">
  <meta name="author" content="CodeHim">
  <title> Password Strength Meter (Vanilla JS) Example </title>

  <link rel='stylesheet' href='https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/css/bootstrap.min.css'>
</head>
<body>
<header class="cd__intro">
  <h1> Password Strength Meter (Vanilla JS) Example </h1>
  <p> Password Strength Meter (Vanilla JS) </p>
  <div class="cd__action">
    <a href="https://www.codehim.com/bootstrap/bootstrap-5-password-strength-meter" title="Back to download and tutorial page" class="cd__btn back">Back to Tutorial</a>
  </div>
</header>
<!--$%adsense%$-->
<main class="cd__main">
  <!-- Start DEMO HTML (Use the following code into your project)-->
  <body class="container-fluid bg-body-tertiary d-block">
  <div class="row justify-content-center">
    <div class="row justify-content-center">
      <div class="col-12 col-md-6 col-lg-4" style="min-width: 500px;">
        <div class="card bg-white mb-5 mt-5 border-0" style="box-shadow: 0 12px 15px rgba(0, 0, 0, 0.02);">
          <div class="card-body p-5">

            <label for="password-input" class="form-label fw-bold">Password</label>
            <input type="password" class="form-control" id="password-input" autocomplete="off"
                   aria-autocomplete="list"
                   aria-label="Password" aria-describedby="passwordHelp">
            <div class="password-meter">
              <div class="meter-section rounded me-2"></div>
              <div class="meter-section rounded me-2"></div>
              <div class="meter-section rounded me-2"></div>
              <div class="meter-section rounded"></div>
            </div>
            <div id="passwordHelp" class="form-text text-muted">Use 8 or more characters with a mix of
              letters, numbers &
              symbols.
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
  </body>
  <!-- END EDMO HTML (Happy Coding!)-->
</main>
<footer class="cd__credit">Author: Marcus - Distributed By: <a title="Free web design code & scripts" href="https://www.codehim.com?source=demo-page" target="_blank">CodeHim</a></footer>


<!--$%analytics%$-->
</body>
</html>
