import { Component } from '@angular/core';
declare function calculateStrength(): void;

@Component({
  selector: 'app-registrazione',
  templateUrl: './registrazione.component.html',
  styleUrls: ['./registrazione.component.css']
})
export class RegistrazioneComponent {
  password: string = '';
  strength: number = 0;

  onPasswordChange() {
    // Chiamata alla funzione di logica JavaScript per calcolare la forza della password
    this.strength = calculateStrength(this.password);
  }


}
<!DOCTYPE html>
<html lang="it">
<link rel='stylesheet' href='https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/css/bootstrap.min.css'>
<script src='https://cdn.jsdelivr.net/npm/@popperjs/core@2.11.8/dist/umd/popper.min.js'></script>
<script src='https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/js/bootstrap.min.js'></script>

<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">

</head>
<body>
<!-- password-strength.component.html -->
<div>
  <label for="password">Password:</label>
  <input type="password" id="password" [(ngModel)]="password" (input)="onPasswordChange()" />

  <div *ngIf="strength > 0" class="strength-meter">
    <div [style.width.%]="strength" [ngStyle]="getStrengthColor()">
      Forza: {{ strength }}%
    </div>
  </div>
</div>


</body>
</html>
