import { Component } from '@angular/core';

@Component({
  selector: 'app-gestione',
  templateUrl: './gestione.component.html',
  styleUrls: ['./gestione.component.css']
})
export class GestioneComponent {

}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Gestione Utenti</title>
  <style>


    table {
      width: 100%;
      border-collapse: collapse;
      margin-top: 20px;
    }

    table, th, td {
      border: 1px solid #ddd;
    }

    th, td {
      padding: 12px;
      text-align: left;
    }

    th {
      background-color: #f2f2f2;
    }

    form {
      margin-top: 20px;
    }
  </style>
</head>
<body class="app-routing-module-container">

<h2>Gestione Utenti</h2>


<!-- Tabella per visualizzare gli utenti -->
<h3>Lista Utenti</h3>
<table>
  <thead>
  <tr>
    <th>ID</th>
    <th>Nome</th>
    <th>Cognome</th>
    <th>Email</th>
  </tr>
  </thead>
  <tbody>
  <!-- Qui dovresti iterare sugli utenti dal database e popolare la tabella -->
  <tr>
    <td>1</td>
    <td>Andrea</td>
    <td>Rossi</td>
    <td>mario@example.com</td>
  </tr>
  <!-- Altri utenti... -->
  </tbody>
</table>

</body>
</html>
