import {Component, Input} from '@angular/core';
import {Piatto} from "../model/piatto";

@Component({
  selector: 'app-menu-card',
  templateUrl: './menu-card.component.html',
  styleUrls: ['./menu-card.component.css']
})
export class MenuCardComponent  {
  @Input() piatto?: Piatto;


  onCardClick() {
    // Gestisci l'evento di cl1ic sulla card, se necessario
  }

}

<div class="card w-50 ">
  <div class="card-body">
    <h5 class="card-title">{{ piatto?.nome }}</h5>
    <p class="card-text">{{piatto?.prezzo}}</p>
    <button (onClick)="onCardClick()" class="btn btn-primary">Button</button>
  </div>
</div>
