import { Component } from '@angular/core';

@Component({
  selector: 'app-prenotazioneasporto',
  templateUrl: './prenotazioneasporto.component.html',
  styleUrls: ['./prenotazioneasporto.component.css']

})
export class PrenotazioneasportoComponent {
  tipo=1; 


}
<!DOCTYPE html>
<html lang="it" class="app-routing-module-container">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Prenotazione D'Asporto</title>
  <style>

    form {
      max-width: 600px;
      margin: 0 auto;
      padding: 20px;
      border-radius: 8px;
      box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
    }
    label {
      display: block;
      margin-bottom: 8px;
    }
    input,
    textarea {
      width: 100%;
      padding: 10px;
      margin-bottom: 15px;
      box-sizing: border-box;
      border: 1px solid #ccc;
      border-radius: 4px;
    }
    button {
      background-color: #4caf50;
      color: #fff;
      padding: 12px 20px;
      border: none;
      border-radius: 4px;
      cursor: pointer;
    }
    label.inline {
      display: inline-block;
      margin-right: 20px;
    }
    .dropdown-menu {
      margin-bottom: 15px;
    }
  </style>
</head>
<body>

<form action="#" method="post">
  <h2>Prenotazione D'Asporto</h2>

  <label for="nome">Nome:</label>
  <input type="text" id="nome" name="nome" required>

  <label for="cognome">Cognome:</label>
  <input type="text" id="cognome" name="cognome" required>

  <label for="telefono">Numero di Telefono:</label>
  <input type="tel" id="telefono" name="telefono" required>

  <label for="email">Indirizzo Email:</label>
  <input type="email" id="email" name="email" required>

  <h3>Dettagli dell'Ordine</h3>

  <label for="data_ora">Data e Ora del Ritiro:</label>
  <input type="datetime-local" id="data_ora" name="data_ora" required>

  <label for="categoria">Seleziona la Categoria:</label>
  <select id="categoria" name="categoria">
    <option value="antipasti">Antipasti</option>
    <option value="primi">Primi</option>
  </select>

  <app-sezione-piatti-vetrina [tipo]="antipasti"></app-sezione-piatti-vetrina>



  <h3>Preferenze Aggiuntive</h3>

  <label for="allergie">Allergie o Intolleranze Alimentari:</label>
  <input type="text" id="allergie" name="allergie">

  <label for="richieste_speciali">Richieste Speciali:</label>
  <textarea id="richieste_speciali" name="richieste_speciali" rows="4"></textarea>

  <h3>Conferma e Invio</h3>
  <div class="accordion" id="menuAccordion1">
    <div class="accordion-item">
      <h2 class="accordion-header" id="menuHeading1">
        <button class="accordion-button" type="button" data-bs-toggle="collapse" data-bs-target="#menuCollapse1" >
          <i class="fas fa-chart-area fa-fw me-3"></i><span>Modalita Consegna</span>
        </button>
      </h2>
      <div id="menuCollapse1" class="accordion-collapse collapse" aria-labelledby="menuHeading1" data-bs-parent="#menuAccordion1">
        <div class="accordion-body">
          <a class="item1 d-block" routerLink="/antipasti">Ritiro in ristorante</a>
          <a class="item1 d-block" routerLink="/primi">Consegna</a>

          <!-- Aggiungi più elementi del menu se necessario -->
        </div>
      </div>
    </div>
  </div>



  <button type="submit">Invia Prenotazione</button>
</form>

</body>
</html>
