import {Component, Input} from '@angular/core';
import {Piatto} from "../model/piatto";
import {consolidateMessages} from "@angular/localize/tools/src/extract/translation_files/utils";
import {CarrelloServiceService} from "../services/carrello-service.service";

@Component({
  selector: 'app-menu-card',
  templateUrl: './menu-card.component.html',
  styleUrls: ['./menu-card.component.css']
})
export class MenuCardComponent  {
  @Input() piatto?: Piatto;

  @Input() aggiungiAlCarrelloFn?: Function;



  constructor(private carrelloService: CarrelloServiceService) {
  }


  onCardClick() {
    this.carrelloService.addPiatto(this.piatto);
    this.aggiungiAlCarrello()
  }

  aggiungiAlCarrello() {
    // Chiamare la funzione passata come input
    if (this.aggiungiAlCarrelloFn) {
      this.aggiungiAlCarrelloFn(this.piatto);
    }
  }

}

<div class="card w-50 ">
  <div class="card-body">
    <h5 class="card-title">{{ piatto?.nome }}</h5>
    <p class="card-text">{{piatto?.prezzo}}</p>
    <button (click)="onCardClick()" class="btn btn-primary">Button</button>
  </div>
</div>
