import {Component, Input} from '@angular/core';
import {Piatto} from "../model/piatto";
import {PiattiServiceService} from "../piatti-service.service";

@Component({
  selector: 'app-piatti-vetrina',
  templateUrl: './piatti-vetrina.component.html',
  styleUrls: ['./piatti-vetrina.component.css']
})
export class PiattiVetrinaComponent {
  @Input()piatto?: Piatto;


}
<!DOCTYPE html>
<html lang="en">

<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Delicious Recipe</title>
  <!-- Add your custom stylesheets or link to external stylesheets here -->
  <link rel="stylesheet" href="https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css">
  <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/5.15.3/css/all.min.css">
  <style>
    body {
      font-family: 'Arial', sans-serif;
      background-color: #f8f9fa;
      color: #495057;
      margin: 0;
      padding: 0;
    }

    header {
      background-color: #343a40;
      color: #ffffff;
      text-align: center;
      padding: 20px 0;
    }

    .recipe {
      max-width: 800px;
      margin: 20px auto;
      background-color: #ffffff;
      box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
      border-radius: 8px;
      overflow: hidden;
    }

    .recipe-image img {
      width: 100%;
      height: auto;
      border-bottom: 1px solid #dee2e6;
    }

    .recipe-details {
      padding: 20px;
    }

    h2, h3 {
      color: #343a40;
    }

    .description {
      color: #6c757d;
    }

    ul, ol {
      padding-left: 20px;
    }

    footer {
      text-align: center;
      padding: 20px 0;
      background-color: #343a40;
      color: #ffffff;
    }
  </style>
</head>

<body>
<header>
  <h1>Delicious Recipe</h1>
  <p>Enjoy cooking and savoring this delightful dish!</p>
</header>

<section class="recipe">
  <div class="recipe-image">
    <img src="https://via.placeholder.com/800x400" alt="Recipe Image">
  </div>

  <div class="recipe-details">
    <h2>Recipe Title</h2>
    <p class="description">Description of the recipe. You can add more details here.</p>

    <h3>Ingredients</h3>
    <ul>
      <li>Ingredient 1</li>
      <li>Ingredient 2</li>
      <li>Ingredient 3</li>
      <!-- Add more ingredients as needed -->
    </ul>

    <h3>Instructions</h3>
    <ol>
      <li>Step 1: Instructions for step 1.</li>
      <li>Step 2: Instructions for step 2.</li>
      <li>Step 3: Instructions for step 3.</li>
      <!-- Add more steps as needed -->
    </ol>

    <div class="tips">
      <h3>Tips</h3>
      <p>Include any additional tips or notes for the recipe.</p>
    </div>
  </div>
</section>

<footer>
  <p>&copy; 2023 Your Recipe Website. All rights reserved.</p>
</footer>
</body>

</html>
