import {Component, OnInit} from '@angular/core';
import {ActivatedRoute} from "@angular/router";

@Component({
  selector: 'app-html-create',
  templateUrl: './html-create.component.html',
  styleUrls: ['./html-create.component.css']
})
export class HtmlCreateComponent implements OnInit{
  name: string | null = null;

  constructor(private route: ActivatedRoute) {}

  ngOnInit(): void {
    // Retrieve the ID parameter from the route
    this.name = this.route.snapshot.paramMap.get('name');

    // Now 'this.id' contains the ID value
    console.log('ID:', this.name);
  }
}
<!DOCTYPE html>
<html lang="it">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Ricetta del tuo piatto preferito</title>
  <style>


    header {
      text-align: center;
      background-color: #ffcc80;
      padding: 20px;
    }

    h1 {
      color: #fff;
      margin-bottom: 10px;
    }

    p {
      margin: 0;
    }

    section {
      max-width: 800px;
      margin: 20px auto;
      background-color: #fff;
      padding: 20px;
      border-radius: 8px;
      box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
    }

    h2 {
      color: #ffcc80;
    }

    ul, ol {
      margin: 0;
      padding-left: 20px;
    }

    li {
      margin-bottom: 8px;
    }

    img {
      max-width: 100%;
      height: auto;
      border-radius: 8px;
      margin-top: 20px;
    }

    footer {
      text-align: center;
      padding: 10px;
      background-color: #ffcc80;
      margin-top: auto;
    }
  </style>
</head>
<body>
<header>
  <h1>{{ name }}</h1>
  <p>Una deliziosa ricetta da provare a casa!</p>
</header>

<section class="ingredients">
  <h2>Ingredienti:</h2>
  <ul>
    <li>Quantità - Ingrediente 1</li>
    <li>Quantità - Ingrediente 2</li>
    <li>Quantità - Ingrediente 3</li>
    <!-- Aggiungi altri ingredienti se necessario -->
  </ul>
</section>

<section class="instructions">
  <h2>Istruzioni:</h2>
  <ol>
    <li>Passo 1: Descrizione del passo 1.</li>
    <li>Passo 2: Descrizione del passo 2.</li>
    <li>Passo 3: Descrizione del passo 3.</li>
    <!-- Aggiungi altri passi se necessario -->
  </ol>
</section>

<section class="image">
  <img src="https://i.ibb.co/28PScvn/5.jpg" alt="Immagine del piatto">
</section>

<footer>
  <p>Prova questa ricetta e goditi il tuo pasto!</p>
</footer>
</body>
</html>
