import { Component } from '@angular/core';

@Component({
  selector: 'app-prenota',
  templateUrl: './prenota.component.html',
  styleUrls: ['./prenota.component.css']
})
export class PrenotaComponent {

}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <style>
    body, html {
      height: 100%;
      margin: 0;
      overflow: hidden;
    }

    .container {
      display: flex;
      height: 100%;
    }

    .box {
      flex: 1;
      border: 1px solid #ccc;
      transition: flex 0.5s;
      cursor: pointer;
    }

    .box:hover {
      flex: 0 0 100%;
      cursor: pointer;
    }
  </style>
</head>
<body>
<div class="container">
  <div class="box" onclick="alert('Hai cliccato sulla parte sinistra!')">Parte Sinistra</div>
  <div class="box" onclick="alert('Hai cliccato sulla parte destra!')">Parte Destra</div>
</div>
</body>
</html>
