import { Injectable } from '@angular/core';
import {HttpClient} from "@angular/common/http";
import {AuthServiceService} from "./auth-service.service";
import {Piatto} from "../model/piatto";

@Injectable({
  providedIn: 'root'
})
export class CarrelloServiceService {

  constructor(private http:HttpClient,private auth:AuthServiceService) { }

  addPiatto(piatto: Piatto | undefined) {
    console.log("STAMPO PIATTO " + piatto?.nome)
    this.http.post("http://localhost:8080/addCarrello/"+piatto, {"Authorization":"Basic " + this.auth.token}, {withCredentials: true}).subscribe(
      res => {
        console.log("STAMPO RESPONSE " + res)
      }
    );

  }
}
