import { Component } from '@angular/core';
declare const calculateStrength: (password: string) => number; // Importa la funzione dalla logica JavaScript

@Component({
  selector: 'app-registrazione',
  templateUrl: './registrazione.component.html',
  styleUrls: ['./registrazione.component.css']
})
export class RegistrazioneComponent {
  password: string = '';
  strength: number = 0;

  onPasswordChange() {
    this.strength = calculateStrength(this.password);
  }

  getStrengthColor() {
    // Restituisci uno stile CSS in base alla forza della password, ad esempio, un gradiente di colore
    const color = this.strength < 50 ? 'red' : 'green';
    return { 'background': `linear-gradient(to right, ${color} ${this.strength}%, #eee ${this.strength}%)` };
  }


}
<!DOCTYPE html>
<html lang="it">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Registrazione</title>
  <style>
    body {
      font-family: 'Arial', sans-serif;
      background-color: #f4f4f4;
      margin: 0;
      padding: 0;
      display: flex;
      justify-content: center;
      align-items: center;
      height: 100vh;
    }

    form {
      background-color: #fff;
      border-radius: 8px;
      padding: 20px;
      box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
      max-width: 400px;
      width: 100%;
    }

    h2 {
      text-align: center;
      color: #333;
    }

    label {
      display: block;
      margin-bottom: 8px;
      color: #555;
    }

    input {
      width: 100%;
      padding: 10px;
      margin-bottom: 15px;
      box-sizing: border-box;
      border: 1px solid #ccc;
      border-radius: 4px;
    }

    input[type="date"] {
      padding: 10px 5px;
    }

    button {
      background-color: #4caf50;
      color: #fff;
      padding: 10px;
      border: none;
      border-radius: 4px;
      cursor: pointer;
      font-size: 16px;
      width: 100%;
    }

    button:hover {
      background-color: #45a049;
    }

    .radio-group {
      margin-bottom: 15px;
    }

    label.radio {
      display: flex;
      align-items: center;
      color: #555;
    }

    input[type="radio"] {
      margin-right: 8px;
    }

    /* Nuovo stile per la password */
    .strength-meter {
      margin-top: 10px;
      margin-bottom: 15px;
    }

    .strength-meter div {
      padding: 5px;
      border-radius: 4px;
      color: #fff;
      font-weight: bold;
    }
  </style>
</head>
<body>

<form action="process.php" method="post">
  <h2>Registrazione</h2>


    <!-- Nome -->
    <label for="nome">Nome:</label>
    <input type="text" id="nome" name="nome" required>

    <!-- Cognome -->
    <label for="cognome">Cognome:</label>
    <input type="text" id="cognome" name="cognome" required>

    <!-- Data di nascita -->
    <label for="data_nascita">Data di nascita:</label>
    <input type="date" id="data_nascita" name="data_nascita" required>

    <!-- Indirizzo -->
    <label for="indirizzo">Indirizzo:</label>
    <input type="text" id="indirizzo" name="indirizzo" required>

    <!-- Email -->
    <label for="email">Email:</label>
    <input type="email" id="email" name="email" required>

    <!-- Utente -->
    <label for="utente">Utente:</label>
    <input type="text" id="utente" name="utente" required>

    <div>
    <label for="password">Password:</label>
    <input type="password" id="password" [(ngModel)]="password" (input)="onPasswordChange()" />

    <div *ngIf="strength > 0" class="strength-meter">
      <div [style.width.%]="strength" [ngStyle]="getStrengthColor()">
        Forza: {{ strength }}%
      </div>
    </div>
    </div>


    <!-- Ruolo -->
    <label>Ruolo:</label>
    <label for="cliente">
      <input type="radio" id="cliente" name="ruolo" value="cliente" checked>
      Sei un cliente
    </label>
    <label for="proprietario">
      <input type="radio" id="proprietario" name="ruolo" value="proprietario">
      Sei un proprietario
    </label>

    <!-- Pulsante di invio -->
    <button type="submit">Registrati</button>


  <!-- Altri campi del form -->

  <!-- Nuovo campo password personalizzato -->


  <!-- Altri campi del form -->

  <!-- Pulsante di invio -->
  <button type="submit">Registrati</button>
</form>

</body>
</html>
