import { Component } from '@angular/core';

@Component({
  selector: 'app-prenotazioneasporto',
  templateUrl: './prenotazioneasporto.component.html',
  styleUrls: ['./prenotazioneasporto.component.css']
})
export class PrenotazioneasportoComponent {

}
<!DOCTYPE html>
<html lang="it">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Prenotazione D'Asporto</title>

  <!-- Aggiunta di Bootstrap CSS -->
  <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/css/bootstrap.min.css" rel="stylesheet">

  <style>
    body {
      font-family: 'Arial', sans-serif;
      background-color: #f4f4f4;
      margin: 20px;
    }

    .form-container {
      max-width: 600px;
      margin: 0 auto;
      background-color: #fff;
      padding: 20px;
      border-radius: 8px;
      box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
    }

    .form-group {
      margin-bottom: 15px;
    }

    .form-group label {
      font-weight: bold;
    }

    .form-group select {
      width: 100%;
      padding: 10px;
      border: 1px solid #ccc;
      border-radius: 4px;
    }

    .ingredient-container {
      margin-top: 10px;
    }

    .ingredient-group {
      display: flex;
      align-items: center;
      margin-bottom: 10px;
    }

    .ingredient-group label {
      margin-right: 10px;
    }

    .ingredient-group input {
      width: 70px;
    }

    .submit-btn {
      background-color: #4caf50;
      color: #fff;
      padding: 12px 20px;
      border: none;
      border-radius: 4px;
      cursor: pointer;
    }
  </style>
</head>
<body>

<div class="container form-container">
  <h3 class="mb-4">Prenotazione D'Asporto</h3>

  <form action="#" method="post">

    <div class="form-group">
      <label for="ristorante">Nome del Ristorante:</label>
      <input type="text" class="form-control" id="ristorante" name="ristorante" required>
    </div>

    <div class="form-group">
      <label for="data_ora">Data e Ora del Ritiro:</label>
      <input type="datetime-local" class="form-control" id="data_ora" name="data_ora" required>
    </div>

    <div class="form-group">
      <label for="categoria">Seleziona la Categoria:</label>
      <select class="form-control" id="categoria" name="categoria" required>
        <option value="antipasti">Antipasti</option>
        <option value="contorni">Contorni</option>
        <option value="primi">Primi</option>
      </select>
    </div>

    <div class="ingredient-container">
      <div class="ingredient-group">
        <label for="ingrediente">Seleziona l'Ingrediente:</label>
        <select class="form-control" id="ingrediente" name="ingredienti[]" required>
          <option value="Ingrediente1">Ingrediente 1</option>
          <option value="Ingrediente2">Ingrediente 2</option>
          <!-- Aggiungi altri ingredienti secondo necessità -->
        </select>
        <label for="quantita">Quantità:</label>
        <input type="number" class="form-control" id="quantita" name="quantita[]" min="0" placeholder="Quantità" required>
      </div>
    </div>

    <!-- Aggiungi altri gruppi di ingredienti secondo necessità -->

    <div class="form-group">
      <label for="note">Note Aggiuntive:</label>
      <textarea class="form-control" id="note" name="note" rows="4"></textarea>
    </div>

    <button type="submit" class="submit-btn">Invia Prenotazione</button>
  </form>
</div>

<!-- Aggiunta di Bootstrap JS (opzionale, richiede Popper.js) -->
<script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/js/bootstrap.min.js"></script>

</body>
</html>
