import { Component } from '@angular/core';

@Component({
  selector: 'app-searchbar',
  template: `
    <input type="text" [(ngModel)]="searchTerm" [ngbTypeahead]="search">
  `



})
export class SearchbarComponent {
  searchTerm: string = '';

  search = (text$: Observable<string>) =>
    text$.pipe(
      debounceTime(200),
      distinctUntilChanged(),
      switchMap(term =>
        // Implementa la logica di autocompletamento qui
        // Puoi chiamare un servizio Angular che restituisce suggerimenti
        // in base al termine di ricerca
        this.yourSearchService.getSuggestions(term)
      )
    );

}
