import { Injectable } from '@angular/core';
import {HttpClient, HttpHeaders} from "@angular/common/http";
import {AuthServiceService} from "./auth-service.service";
import {Observable} from "rxjs";

@Injectable({
  providedIn: 'root'
})
export class UtenteServiceService {
  private backendUrl = "http://localhost:8080"


  constructor(private http:HttpClient,private auth:AuthServiceService) {  }

  checkUtente(name:string):Observable<boolean>{
    var header  = {
      headers: new HttpHeaders().set('Authorization', 'Basic ' + this.auth.token)
    }
    console.log("entrato")
    return this.http.get<boolean>(this.backendUrl+ "/checkUtente/" + name, header)

  }
}
