import {Component, OnInit} from '@angular/core';
import {ActivatedRoute} from "@angular/router";

@Component({
  selector: 'app-html-create',
  templateUrl: './html-create.component.html',
  styleUrls: ['./html-create.component.css']
})
export class HtmlCreateComponent implements OnInit{
  name: string | null = null;

  constructor(private route: ActivatedRoute) {}

  ngOnInit(): void {
    // Retrieve the ID parameter from the route
    this.name = this.route.snapshot.paramMap.get('name');

    // Now 'this.id' contains the ID value
    console.log('ID:', this.name);
  }
}
<!DOCTYPE html>
<html lang="it">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Presentation della Ricetta</title>
  <style>
    body {
      font-family: 'Arial', sans-serif;
      background-color: #f8f8f8;
      margin: 0;
      padding: 0;
      color: #333;
      display: flex;
      flex-direction: column;
      align-items: center;
    }

    header {
      text-align: center;
      background-color: #ffcc80;
      padding: 20px;
      width: 100%;
      box-sizing: border-box;
    }

    h1 {
      color: #fff;
      margin-bottom: 10px;
    }

    .recipe-card {
      max-width: 600px;
      margin: 20px;
      background-color: #fff;
      border-radius: 8px;
      box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
      overflow: hidden;
    }

    .recipe-image {
      max-width: 100%;
      height: auto;
      border-radius: 8px 8px 0 0;
    }

    .card-content {
      padding: 20px;
    }

    h2 {
      color: #ffcc80;
    }

    .card-text {
      color: #555;
    }

    footer {
      text-align: center;
      padding: 10px;
      background-color: #ffcc80;
      width: 100%;
      box-sizing: border-box;
      margin-top: auto;
    }
  </style>
</head>
<body class="app-routing-module-container">
<header>
  <h1>Nome della Ricetta</h1>
</header>

<div class="recipe-card">
  <img src="https://i.ibb.co/28PScvn/5.jpg" class="recipe-image" alt="Immagine della ricetta">
  <div class="card-content">
    <h2>Card title</h2>
    <p class="card-text">This is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
    <p class="card-text">Last updated 3 mins ago</p>
  </div>
</div>

<!-- Aggiungi altre card per ingredienti, istruzioni, ecc. -->

<footer>
  <p>Prova questa deliziosa ricetta e goditi il tuo pasto!</p>
</footer>
</body>
</html>
