import { Component } from '@angular/core';

@Component({
  selector: 'app-registrazione',
  templateUrl: './registrazione.component.html',
  styleUrls: ['./registrazione.component.css']
})
export class RegistrazioneComponent {

}
<!DOCTYPE html>
<html lang="it">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Registrazione</title>
  <style>
    body {
      font-family: 'Arial', sans-serif;
      background-color: #f4f4f4;
      margin: 0;
      padding: 0;
      display: flex;
      justify-content: center;
      align-items: center;
      height: 100vh;
    }

    form {
      background-color: #fff;
      border-radius: 8px;
      padding: 20px;
      box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
      max-width: 400px;
      width: 100%;
    }

    h2 {
      text-align: center;
      color: #333;
    }

    label {
      display: block;
      margin-bottom: 8px;
      color: #555;
    }

    input {
      width: 100%;
      padding: 10px;
      margin-bottom: 15px;
      box-sizing: border-box;
      border: 1px solid #ccc;
      border-radius: 4px;
    }

    input[type="date"] {
      padding: 10px 5px;
    }

    button {
      background-color: #4caf50;
      color: #fff;
      padding: 10px;
      border: none;
      border-radius: 4px;
      cursor: pointer;
      font-size: 16px;
      width: 100%;
    }

    button:hover {
      background-color: #45a049;
    }

    .radio-group {
      margin-bottom: 15px;
    }

    label.radio {
      display: flex;
      align-items: center;
      color: #555;
    }

    input[type="radio"] {
      margin-right: 8px;
    }
  </style>
</head>
<body >

<form action="process.php" method="post">
  <h2>Registrazione</h2>

  <!-- Nome -->
  <label for="nome">Nome:</label>
  <input type="text" id="nome" name="nome" required>

  <!-- Cognome -->
  <label for="cognome">Cognome:</label>
  <input type="text" id="cognome" name="cognome" required>

  <!-- Data di nascita -->
  <label for="data_nascita">Data di nascita:</label>
  <input type="date" id="data_nascita" name="data_nascita" required>

  <!-- Indirizzo -->
  <label for="indirizzo">Indirizzo:</label>
  <input type="text" id="indirizzo" name="indirizzo" required>

  <!-- Email -->
  <label for="email">Email:</label>
  <input type="email" id="email" name="email" required>

  <!-- Utente -->
  <label for="utente">Utente:</label>
  <input type="text" id="utente" name="utente" required>

  <!-- Password -->
  <div>
    <div class="form-group">
      <label for="exampleInputPassword">Password con label, placeholder e testo di aiuto</label>
      <input type="password" data-bs-input class="form-control input-password" id="exampleInputPassword" aria-labelledby="infoPassword">
      <span class="password-icon" aria-hidden="true">
      <svg class="password-icon-visible icon icon-sm"><use href="/bootstrap-italia/dist/svg/sprites.svg#it-password-visible"></use></svg>
      <svg class="password-icon-invisible icon icon-sm d-none"><use href="/bootstrap-italia/dist/svg/sprites.svg#it-password-invisible"></use></svg>
    </span>
      <small id="infoPassword" class="form-text">Inserisci almeno 8 caratteri e una lettera maiuscola</small>
    </div>
    <div class="form-group">
      <label for="exampleInputPassword3">Password con strength meter</label>
      <input type="password" data-bs-input class="form-control input-password" id="exampleInputPassword3">
      <div class="password-strength-meter">
        <small class="form-text text-muted"
               data-bs-short-pass="Password molto debole"
               data-bs-bad-pas="Password debole"
               data-bs-good-pass="Password sicura"
               data-bs-strong-pass="Password molto sicura"
        >Inserisci almeno 8 caratteri e una lettera maiuscola</small>
        <div class="password-meter progress rounded-0 position-absolute">
          <div class="row position-absolute w-100 m-0">
            <div class="col-3 border-start border-end border-white"></div>
            <div class="col-3 border-start border-end border-white"></div>
            <div class="col-3 border-start border-end border-white"></div>
            <div class="col-3 border-start border-end border-white"></div>
          </div>
          <div class="progress-bar bg-muted" role="progressbar" aria-valuenow="0" aria-valuemin="0" aria-valuemax="100"></div>
        </div>
      </div>
      <span class="password-icon" aria-hidden="true">
      <svg class="password-icon-visible icon icon-sm"><use href="/bootstrap-italia/dist/svg/sprites.svg#it-password-visible"></use></svg>
      <svg class="password-icon-invisible icon icon-sm d-none"><use href="/bootstrap-italia/dist/svg/sprites.svg#it-password-invisible"></use></svg>
    </span>
      <small class="password-caps form-text text-warning position-absolute bg-white w-100">CAPS LOCK inserito</small>
    </div>
  </div>

  <!-- Ruolo -->
  <div class="radio-group">
    <label class="radio">
      <input type="radio" id="cliente" name="ruolo" value="cliente" checked>
      Sei un cliente
    </label>
    <label class="radio">
      <input type="radio" id="proprietario" name="ruolo" value="proprietario">
      Sei un proprietario
    </label>
  </div>

  <!-- Pulsante di invio -->
  <button type="submit">Registrati</button>
</form>

</body>
</html>
