import {Component, Input} from '@angular/core';
import {Piatto} from "../model/piatto";

@Component({
  selector: 'app-sezione-menu-vetrina',
  templateUrl: './sezione-menu-vetrina.component.html',
  styleUrls: ['./sezione-menu-vetrina.component.css']
})
export class SezioneMenuVetrinaComponent {
  piatti?:Piatto[]

}
<section class="app-routing-module-container">
  <h3>Antipasti: </h3>
  <app-piatti-vetrina   *ngFor="let piat of piatti" [piatto]="piat"></app-piatti-vetrina>
</section>
