import {Component, OnInit} from '@angular/core';
import {ActivatedRoute} from "@angular/router";
import {Piatto} from "../model/piatto";
import {PiattiServiceService} from "../piatti-service.service";
import {Observable} from "rxjs";

@Component({
  selector: 'app-html-create',
  templateUrl: './html-create.component.html',
  styleUrls: ['./html-create.component.css']
})
export class HtmlCreateComponent implements OnInit{
  name: string | null = this.route.snapshot.paramMap.get('name');
  piatto : Piatto | null = null ;
  mostraRecensione: boolean = false;
  testoRecensione: string = '';

  toggleRecensione() : void {
    this.mostraRecensione = !this.mostraRecensione;
  }

  chiudiRecensione() : void {
    this.mostraRecensione = false;}




    constructor(private route: ActivatedRoute, private piattiservice:PiattiServiceService) {}

  ngOnInit(): void {
    // Retrieve the ID parameter from the route
    this.caricaPiattoDesiderato(this.name!)

  }
  caricaPiattoDesiderato(nomePiatto: string): void {
    this.piattiservice.dammiPiatto(nomePiatto).subscribe(
      (piatto) => {
        this.piatto = piatto;
        console.log('Piatto ottenuto:', piatto);
      },
      (error) => {
        console.error('Errore durante la richiesta del piatto:', error);
      }
    );







}}
<!-- Your HTML content -->

<div class="container mt-4">
  <div class="row">
    <div class="col-md-4 mb-4">
      <div id="app" class="card bg-warning text-white">
        <img class="card-img-top" [src]="piatto?.immagine" alt="Card image cap">
      </div>
    </div>
    <div class="col-md-8 mb-4">
      <div class="card">
        <div class="card-body">
          <h5 class="card-title">{{ piatto?.nome}} </h5>
          <p class="card-text text-justify">{{piatto?.descrizione}} </p>
        </div>
        <div class="card-body">
          <a [routerLink]="['/html', piatto?.nome]" *ngIf="isNotBevanda()">Vedi Dettagli</a>

          <!-- Recensione Button -->
          <button class="btn btn-primary mr-2" (click)="toggleRecensione()">Recensisci</button>

          <!-- Recensione Textbox -->
          <div *ngIf="mostraRecensione">
            <textarea class="form-control mb-2" rows="3" placeholder="Inserisci la tua recensione" [(ngModel)]="testoRecensione"></textarea>
            <button class="btn btn-secondary" (click)="chiudiRecensione()">Chiudi</button>
          </div>

          <span class="float-right align-middle font-weight-bold">
            {{piatto?.prezzo}} €
            <i class="fas fa-star text-warning fa-lg"></i>
          </span>
        </div>
      </div>
    </div>
  </div>
</div>
