import {Component, OnInit} from '@angular/core';
import {ActivatedRoute} from "@angular/router";
import {Piatto} from "../model/piatto";
import {PiattiServiceService} from "../piatti-service.service";
import {Observable} from "rxjs";

@Component({
  selector: 'app-html-create',
  templateUrl: './html-create.component.html',
  styleUrls: ['./html-create.component.css']
})
export class HtmlCreateComponent implements OnInit{
  name: string | null = this.route.snapshot.paramMap.get('name');
  piatto : Observable<Piatto> | undefined;

  constructor(private route: ActivatedRoute, private piattiservice:PiattiServiceService) {}

  ngOnInit(): void {
    // Retrieve the ID parameter from the route

    this.piatto = this.piattiservice.dammiPiatto(this.name);

    // Now 'this.id' contains the ID value
    console.log('ID:', this.name);

  }
}
<!DOCTYPE html>
<html lang="it">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Ricetta del piatto delizioso</title>
  <style>

    .card {
      border: 1px solid #ccc;
      border-radius: 8px;
      margin-bottom: 20px;
      padding: 20px;
      box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
      flex: 1;
      display: flex;
    }

    img {
      max-width: 100%;
      height: auto;
      border-radius: 8px;
      margin-right: 20px; /* Aggiunto uno spazio a destra dell'immagine */
    }
  </style>
</head>
<body class="app-routing-module-container">

<!-- Card dell'immagine e descrizione -->
<div class="card">
  <img src="https://i.ibb.co/28PScvn/5.jpg" alt="Immagine della ricetta" style="width: 540px">
  <div>
    <h2>Descrizione breve della ricetta:</h2>
    <p>Questo piatto è delizioso e facile da preparare. Contiene ingredienti freschi e gustosi che renderanno il tuo pasto indimenticabile.</p>
  </div>
</div>

<!-- Card degli ingredienti -->
<div class="card">
  <h2>Ingredienti:</h2>
  <ul>
    <li>200g di ingredienti principali</li>
    <li>50g di ingrediente numero uno</li>
    <li>1 cucchiaino di ingrediente segreto</li>
    <!-- Aggiungi altri ingredienti secondo necessità -->
  </ul>
</div>

<!-- Card della procedura di preparazione -->
<div class="card">
  <h2>Procedura di preparazione:</h2>
  <ol>
    <li>Passo 1: Inizia con...</li>
    <li>Passo 2: Aggiungi...</li>
    <li>Passo 3: Cuoci a fuoco medio per...</li>
    <!-- Aggiungi altri passaggi secondo necessità -->
  </ol>
</div>

</body>
</html>
