import {Component, Input} from '@angular/core';

@Component({
  selector: 'app-menu-card',
  templateUrl: './menu-card.component.html',
  styleUrls: ['./menu-card.component.css']
})
export class MenuCardComponent {
  @Input() vegetableName?: string;
  @Input() vegetableImageUrl?: string;
  @Input() vegetablePrice?: string;

  onCardClick() {
    // Gestisci l'evento di cl1ic sulla card, se necessario
  }

}
<div class="vegetable-card" (click)="onCardClick()">
  <div class="vegetable-name">{{ vegetableName }}</div>
  <img [src]="'https://i.ibb.co/hmcmsch/29.jpg'" alt="Vegetable Image">
  <div class="vegetable-price">{{ vegetablePrice }}</div>
</div>
