import {Component, OnInit} from '@angular/core';
import {PiattiServiceService} from "../piatti-service.service";
import {Ingrediente} from "../model/ingrediente";
import {Piatto} from "../model/piatto";
import {HamburgerServiceService} from "../services/hamburger-service.service";
import {Hamburger} from "../model/hamburger";

@Component({
  selector: 'app-creapiatto',
  templateUrl: './creapiatto.component.html',
  styleUrls: ['./creapiatto.component.css']
})
export class CreapiattoComponent implements OnInit{
  base?:Ingrediente[]
  ingrediente?:Ingrediente[]
  salsa?:Ingrediente[]
  carne?:Ingrediente[]
  contorno?:Piatto[]
  bevanda?:Piatto[]
  hamburger?:Hamburger[]

  constructor(private piattiService:PiattiServiceService, private hamburgerService:HamburgerServiceService){}


  ngOnInit(){
    this.getIngredienti();
    this.getCarrello();
  }

  private getIngredienti() {

    this.hamburgerService.dammiBase().subscribe(
      base => this.base = base
    );
    this.hamburgerService.dammiIngredienti().subscribe(
      ingrediente => this.ingrediente = ingrediente
    );
    this.hamburgerService.dammiSalsa().subscribe(
      salsa => this.salsa = salsa
    );
    this.hamburgerService.dammiCarne().subscribe(
      carne => this.carne = carne
    );
    this.piattiService.dammiContorni().subscribe
    (piatti => this.contorno = piatti);

    this.piattiService.dammiBevande().subscribe
    (piatti => this.bevanda = piatti);

  }

  public getCarrello(){
    this.hamburgerService.getHamburger().subscribe
    (hamburger => this.hamburger = hamburger);
  }

  calculateTotal(){
    // @ts-ignore
    return this.hamburger.reduce((total, item) => total + item.prezzo * item.quantita, 0);

  }


}



<!DOCTYPE html>
<html lang="it">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Il Tuo Ristorante</title>
  <style>
    /* Stili CSS rimangono invariati */

    .body{
      display: flex;

    }



    .carrello-container {

    }
  </style>
</head>
<body>

<section class="app-routing-module-container">
  <h3>Base Panino: </h3>
  <app-hamburger-card *ngFor = "let item of base" [ingrediente]="item"></app-hamburger-card>

  <h3>Ingredienti: </h3>
  <app-hamburger-card *ngFor = "let item of ingrediente" [ingrediente]="item"></app-hamburger-card>


  <h3>Salsa: </h3>
  <app-hamburger-card *ngFor = "let item of salsa" [ingrediente]="item"></app-hamburger-card>

  <h3>Carne: </h3>

  <app-hamburger-card *ngFor = "let item of carne" [ingrediente]="item" (piattoAggiunto)="getCarrello()"></app-hamburger-card>

  <h3>Contorno: </h3>
  <app-menu-card *ngFor = "let item of contorno" [piatto]="item" (piattoAggiunto)="getCarrello()"></app-menu-card>

 <h3>Bevande: </h3>
  <app-menu-card *ngFor = "let item of bevanda" [piatto]="item" (piattoAggiunto)="getCarrello()"></app-menu-card>

  <section class="carrello-container " style="position: relative" >
    <h2>Il tuo carrello</h2>

    <table>
      <thead>
      <tr>
        <th>Prodotto</th>
        <th>Prezzo</th>
        <th>Quantità</th>
        <th>Totale</th>
      </tr>
      </thead>
      <tbody>
      <tr *ngFor="let item of hamburger">
        <td>{{item.nome}}</td>
        <td>{{item.prezzo}}</td>
        <td>{{item.quantita}}</td>
        <td>{{ item.prezzo * item.quantita | currency:'EUR':'symbol':'1.2-2' }}</td>
      </tr>

      <!-- Aggiungi altre righe per gli altri prodotti nel carrello -->
      </tbody>
      <tfoot>
      <tr>
        <td colspan="3"><strong>Totale</strong></td>
        <td><strong>{{ calculateTotal() | currency:'EUR':'symbol':'1.2-2' }}</strong></td>
      </tr>
      </tfoot>
    </table>

    <button class="checkout-btn">Procedi al pagamento</button>

  </section>

</section>



</body>
</html>
