import { Component } from '@angular/core';
import {ActivatedRoute} from "@angular/router";

@Component({
  selector: 'app-html-create',
  templateUrl: './html-create.component.html',
  styleUrls: ['./html-create.component.css']
})
export class HtmlCreateComponent {
  name: string | null = null;

  constructor(private route: ActivatedRoute) {}

  ngOnInit(): void {
    // Retrieve the name parameter from the route
    this.name = this.route.snapshot.paramMap.get('name');

    // Now 'this.name' contains the name value
    console.log('Name:', this.name);
  }

}
