import { Component } from '@angular/core';

@Component({
  selector: 'app-prenota',
  templateUrl: './prenota.component.html',
  styleUrls: ['./prenota.component.css']
})
export class PrenotaComponent {

}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <style>
    body {
      margin: 0;
      overflow: hidden;
    }

    .container {
      display: flex;
      height: 100vh;
    }

    .image-container {
      flex: 1;
      overflow: hidden;
      position: relative;
    }

    .image {
      width: 100%;
      height: 100%;
      object-fit: cover;
      transition: transform 0.3s ease-in-out;
    }

    .image:hover {
      transform: scale(1.2);
    }
  </style>
</head>
<body>

<div class="container">
  <div class="image-container" id="imageContainer1">
    <img class="image" src="https://i.ibb.co/4sQ70tK/37.jpg" alt="Image 1" onclick="expandImage('imageContainer1')">
  </div>
  <div class="image-container" id="imageContainer2">
    <img class="image" src="https://i.ibb.co/V9xxB6N/38.jpg" alt="Image 2" onclick="expandImage('imageContainer2')">
  </div>
</div>

<script>
  function expandImage(containerId) {
    const container = document.getElementById(containerId);
    const image = container.querySelector('.image');

    // Set the image to cover the entire screen
    image.style.transform = 'scale(1.5)';
    image.style.position = 'fixed';
    image.style.top = '0';
    image.style.left = '0';
    image.style.right = '0';
    image.style.bottom = '0';
    image.style.zIndex = '9999';

    // Add a click event to shrink the image back to its original size
    image.onclick = function () {
      image.style.transform = 'scale(1)';
      image.style.position = 'static';
      image.onclick = function () {
        expandImage(containerId);
      };
    };
  }
</script>

</body>
</html>
