import { Component } from '@angular/core';

@Component({
  selector: 'app-prenota',
  templateUrl: './prenota.component.html',
  styleUrls: ['./prenota.component.css']
})
export class PrenotaComponent {

}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Prenotazione</title>
  <style>
    body {
      font-family: 'Arial', sans-serif;
      background-color: #f4f4f4;
      text-align: center;
      margin: 0;
      padding: 0;
    }

    .form-container {
      display: flex;
      justify-content: space-around;
      margin: 50px 0;
    }

    form {
      max-width: 400px;
      padding: 20px;
      background-color: #fff;
      border-radius: 8px;
      box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
    }

    label {
      display: block;
      margin-bottom: 8px;
    }

    input {
      width: 100%;
      padding: 10px;
      margin-bottom: 15px;
      border: 1px solid #ccc;
      border-radius: 4px;
      box-sizing: border-box;
    }

    button {
      background-color: #4caf50;
      color: #fff;
      padding: 12px 20px;
      border: none;
      border-radius: 4px;
      cursor: pointer;
      font-size: 16px;
    }

    button:hover {
      background-color: #45a049;
    }
  </style>
</head>
<body>

<div class="form-container">
  <form id="tableReservation">
    <h2>Prenota un Posto a Tavola</h2>

    <label for="tableEmail">Email:</label>
    <input type="email" id="tableEmail" name="tableEmail" required>

    <label for="tablePhone">Telefono:</label>
    <input type="tel" id="tablePhone" name="tablePhone" required>

    <label for="tableDate">Giorno di prenotazione:</label>
    <input type="date" id="tableDate" name="tableDate" required>

    <label for="tableTime">Ora di prenotazione:</label>
    <input type="time" id="tableTime" name="tableTime" required>

    <button type="submit">Prenota Posto a Tavola</button>
  </form>

  <form id="takeawayReservation">
    <h2>Prenota Cibo d'Asporto</h2>

    <label for="takeawayEmail">Email:</label>
    <input type="email" id="takeawayEmail" name="takeawayEmail" required>

    <label for="takeawayPhone">Telefono:</label>
    <input type="tel" id="takeawayPhone" name="takeawayPhone" required>

    <label for="takeawayDate">Giorno di prenotazione:</label>
    <input type="date" id="takeawayDate" name="takeawayDate" required>

    <label for="takeawayTime">Ora di prenotazione:</label>
    <input type="time" id="takeawayTime" name="takeawayTime" required>

    <button type="submit">Prenota Cibo d'Asporto</button>
  </form>
</div>

</body>
</html>
