import { Component } from '@angular/core';

@Component({
  selector: 'app-prenota',
  templateUrl: './prenota.component.html',
  styleUrls: ['./prenota.component.css']
})
export class PrenotaComponent {

}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <style>
    body, html {
      background-color: #000;
      margin-left: 220px;


      overflow: hidden;
      display: flex;
      justify-content: center;
      align-items: center;
    }

    .container {
      display: flex;


    }

    .box {
      flex: 1;
      overflow: hidden;
      cursor: pointer;
      position: relative;
    }

    .box img {
      width: 100%;
      height: 100%;
      object-fit: cover;
      transition: transform 0.5s;
    }

    .box:hover img {
      transform: scale(1.5); /* Aumenta la scala per raggiungere lo schermo intero */
      z-index: 1; /* Sovrapponi l'immagine al di sopra delle altre */
    }
  </style>
</head>
<body class="app-routing-module-container">
<div class="container">
  <div class="box" onclick="alert('Hai cliccato sulla parte sinistra!')">
    <img src="https://i.ibb.co/vcYsh3z/Whats-App-Image-2024-01-04-at-16-55-23.jpg" alt="Immagine Sinistra">
  </div>
  <div class="box" onclick="alert('Hai cliccato sulla parte destra!')">
    <img src="https://i.ibb.co/4sQ70tK/37.jpg" alt="Immagine Destra">
  </div>
</div>
</body>
</html>
