import {Component, Input, OnInit} from '@angular/core';
import {Piatto} from "../model/piatto";
import {PiattiServiceService} from "../piatti-service.service";
import {CarrelloServiceService} from "../services/carrello-service.service";
import {Carrello} from "../model/carrello";

@Component({
  selector: 'app-sezione-menu-vetrina',
  templateUrl: './sezione-menu-vetrina.component.html',
  styleUrls: ['./sezione-menu-vetrina.component.css']
})
export class SezioneMenuVetrinaComponent implements OnInit{
  antipasto?:Piatto[]
  primo?:Piatto[]
  secondo?:Piatto[]
  contorno?:Piatto[]
  dolce?:Piatto[]
  senzaLattosio?:Piatto[]
  senzaGlutine?:Piatto[]
  bevanda?:Piatto[]
  carrello?:Carrello[]




  constructor(private piattiService:PiattiServiceService, private carrelloService:CarrelloServiceService){}
  ngOnInit(){
    this.getPiatti();
    this.getCarrello();
  }


  private getPiatti() {
    this.piattiService.dammiAntipasti().subscribe
    (piatti => this.antipasto = piatti);
    this.piattiService.dammiPrimi().subscribe
    (piatti => this.primo = piatti);
    this.piattiService.dammiSecondi().subscribe
    (piatti => this.secondo = piatti);
    this.piattiService.dammiContorni().subscribe
    (piatti => this.contorno = piatti);
    this.piattiService.dammiDolci().subscribe
    (piatti => this.dolce = piatti);
    this.piattiService.dammiSenzaLattosio().subscribe
    (piatti => this.senzaLattosio = piatti);
    this.piattiService.dammiSenzaGlutine().subscribe
    (piatti => this.senzaGlutine = piatti);
    this.piattiService.dammiBevande().subscribe
    (piatti => this.bevanda = piatti);

  }

  public getCarrello() {
    this.carrelloService.getCarrello().subscribe
    (carrello => this.carrello = carrello);


  }

  calculateTotal(): number {
    // @ts-ignore
    return this.carrello.reduce((total, item) => total + item.prezzo * item.quantita, 0);
  }
}



<!DOCTYPE html>
<html lang="it">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Il Tuo Ristorante</title>
  <style>
    /* Stili CSS rimangono invariati */

    .body{
      display: flex;

    }



    .carrello-container {

    }
  </style>
</head>
<body>

<section class="app-routing-module-container">
  <h3>Antipasti: </h3>
  <app-menu-card *ngFor="let item of antipasto" [piatto]="item" (piattoAggiunto)="getCarrello()" ></app-menu-card>

  <h3>Primi: </h3>
  <app-menu-card *ngFor="let item of primo" [piatto]="item" (piattoAggiunto)="getCarrello()"></app-menu-card>

  <h3>Secondi: </h3>
  <app-menu-card *ngFor="let item of secondo" [piatto]="item" (piattoAggiunto)="getCarrello()"></app-menu-card>

  <h3>Contorni: </h3>
  <app-menu-card *ngFor="let item of contorno" [piatto]="item" (piattoAggiunto)="getCarrello()"></app-menu-card>

  <h3>Dolci: </h3>
  <app-menu-card *ngFor="let item of dolce" [piatto]="item" (piattoAggiunto)="getCarrello()"></app-menu-card>

  <h3>Senza Lattosio: </h3>
  <app-menu-card *ngFor="let item of senzaLattosio" [piatto]="item" (piattoAggiunto)="getCarrello()"></app-menu-card>

  <h3>Senza Glutine: </h3>
  <app-menu-card *ngFor="let item of senzaGlutine" [piatto]="item" (piattoAggiunto)="getCarrello()"></app-menu-card>

  <h3>Bevande: </h3>
  <app-menu-card *ngFor="let item of bevanda" [piatto]="item" (piattoAggiunto)="getCarrello()"></app-menu-card>

  <section class="carrello-container " style="position: relative" >
    <h2>Il tuo carrello</h2>

    <table>
      <thead>
      <tr>
        <th>Prodotto</th>
        <th>Prezzo</th>
        <th>Quantità</th>
        <th>Totale</th>
      </tr>
      </thead>
      <tbody>
      <tr *ngFor="let item of carrello">
        <td>{{item.nome}}</td>
        <td>{{item.prezzo}}</td>
        <td>{{item.quantita}}</td>
        <td>{{ item.prezzo * item.quantita | currency:'EUR':'symbol':'1.2-2' }}</td>
      </tr>

      <!-- Aggiungi altre righe per gli altri prodotti nel carrello -->
      </tbody>
      <tfoot>
      <tr>
        <td colspan="3"><strong>Totale</strong></td>
        <td><strong>{{ calculateTotal() | currency:'EUR':'symbol':'1.2-2' }}</strong></td>
      </tr>
      </tfoot>
    </table>

    <button class="checkout-btn">Procedi al pagamento</button>

  </section>

</section>



</body>
</html>
