import {Component, Input} from '@angular/core';
import {Piatto} from "../model/piatto";
import {PiattiServiceService} from "../piatti-service.service";

@Component({
  selector: 'app-piatti-vetrina',
  templateUrl: './piatti-vetrina.component.html',
  styleUrls: ['./piatti-vetrina.component.css']
})
export class PiattiVetrinaComponent {
  @Input()piatto?: Piatto;

  mostraRecensione: boolean = false;
  testoRecensione: string = '';

  toggleRecensione() : void {
    this.mostraRecensione = !this.mostraRecensione;
  }

  chiudiRecensione() : void {
    this.mostraRecensione = false;}

  isNotBevanda(){
    return this.piatto?.tipo != 8;
  }

  aggiungiPreferiti(){
    // Aggiungi qui la logica per gestire l'aggiunta della recensione
    console.log('Stellina cliccata! Aggiungi la tua logica qui.');


  }











}
<div class="container mt-4">
  <div class="row">
    <div class="col-md-4 mb-4">
      <div id="app" class="card bg-warning text-white">
        <img class="card-img-top" [src]="piatto?.immagine" alt="Card image cap">
      </div>
    </div>
    <div class="col-md-8 mb-4">
      <div class="card">
        <div class="card-body">
          <h5 class="card-title">{{ piatto?.nome}} </h5>
          <p class="card-text text-justify">{{piatto?.descrizione}} </p>
        </div>
        <div class="card-body d-flex justify-content-between align-items-center">
          <div>
            <a [routerLink]="['/html', piatto?.nome]" *ngIf="isNotBevanda()">Vedi Dettagli</a>
          </div>

          <div class="d-flex align-items-center ml-auto">
            <button class="btn btn-primary ml-2 transparent-bg" (click)="toggleRecensione()">Scrivi una recensione</button>
            <strong style="margin-left: 10px">{{piatto?.prezzo}} €</strong>

            <button type="button" onClick="aggiungiPreferiti()" style="background-color: transparent; border: none; margin-left: 8px" >
              <div>
                <svg   xmlns="http://www.w3.org/2000/svg" width="25" height="25" fill="currentColor" class="bi bi-star" viewBox="0 0 16 16">
                  <path d="M2.866 14.85c-.078.444.36.791.746.593l4.39-2.256 4.389 2.256c.386.198.824-.149.746-.592l-.83-4.73 3.522-3.356c.33-.314.16-.888-.282-.95l-4.898-.696L8.465.792a.513.513 0 0 0-.927 0L5.354 5.12l-4.898.696c-.441.062-.612.636-.283.95l3.523 3.356-.83 4.73zm4.905-2.767-3.686 1.894.694-3.957a.56.56 0 0 0-.163-.505L1.71 6.745l4.052-.576a.53.53 0 0 0 .393-.288L8 2.223l1.847 3.658a.53.53 0 0 0 .393.288l4.052.575-2.906 2.77a.56.56 0 0 0-.163.506l.694 3.957-3.686-1.894a.5.5 0 0 0-.461 0z"/>
                </svg>

              </div>
            </button>
          </div>
        </div>
      </div>
      <!-- Recensione Textbox -->
      <div *ngIf="mostraRecensione" class="text-right">
        <textarea class="form-control mb-2" rows="3" placeholder="Inserisci la tua recensione" [(ngModel)]="testoRecensione"></textarea>
        <button class="btn btn-secondary" (click)="chiudiRecensione()">Chiudi</button>
      </div>
    </div>
  </div>
</div>
